mapper.update:
UPDATE u_file_record
SET valid       = #{valid},
    update_time = now(),
    update_by   = #{updateBy}
WHERE id = #{id}

UPDATE u_file_record
SET record_table_id = (
    SELECT id
    FROM ${fileRecordEntity.recordTable}
    WHERE valid = 1
      AND ${tableFileCol} LIKE CONCAT('%', #{fileRecordEntity.recordUrl}, '%')
    LIMIT 1
)
WHERE valid = 1
  AND id = #{fileRecordEntity.id}
