mapper.select:
SELECT ucr.id, ucr.resource_title, ucr.resource_summary, ucr.resource_type, ucr.resource_shared, ucr.valid, ucr.memo, ucr.create_time, ucr.create_by, ucr.update_time, ucr.update_by, ucf.id AS file_id, ucf.file_name, ucf.file_size, ucf.file_ext, ucf.file_type, ucf.file_parent_id FROM u_course_file ucf LEFT JOIN u_course_resource ucr ON ucr.valid = 1 AND ucf.id = ucr.file_id WHERE ucf.valid = 1 AND ucf.file_parent_id = #{fileParentId}
AND resource_title LIKE CONCAT('%', #{resourceTitle} , '%')
AND resource_summary LIKE CONCAT('%', #{resourceSummary} , '%')
AND resource_type = #{resourceType}
AND resource_shared = #{resourceShared}
AND ucf.file_name LIKE CONCAT('%', #{fileName} , '%')
AND ucf.file_ext LIKE CONCAT('%', #{fileExt} , '%')
AND ucf.file_type = #{fileType,jdbcType=INTEGER}

mapper.update:
UPDATE u_course_resource
SET update_by   = #{updateBy},
    update_time = now(),
    valid       = #{valid}
WHERE id = #{id}
