mapper.select:
SELECT utt.id, utt.course_id, utt.tea_id, utt.teaching_task_alias, utt.term, utt.valid, utt.memo, utt.create_time, utt.create_by, utt.update_time, utt.update_by, uc.course_number, uc.course_name, uc.course_credit FROM u_teaching_task utt INNER JOIN u_teacher ut ON utt.valid = 1 AND utt.tea_id = ut.id INNER JOIN u_course uc ON uc.valid = 1 AND utt.course_id = uc.id WHERE utt.valid = 1 AND ut.tea_number = #{courseTeachingTaskDto.teaNumber}
AND utt.teaching_task_alias LIKE CONCAT('%', #{courseTeachingTaskDto.teachingTaskAlias}, '%')
AND utt.term = #{courseTeachingTaskDto.term}
AND uc.course_number LIKE CONCAT('%', #{courseTeachingTaskDto.courseNumber}, '%')
AND uc.course_name LIKE CONCAT('%', #{courseTeachingTaskDto.courseName}, '%')
AND uc.course_credit = #{courseTeachingTaskDto.courseCredit}
ORDER BY utt.update_time desc
LIMIT #{pageParam.startIndex}, #{pageParam.pageSize}

SELECT COUNT(1) FROM u_teaching_task utt INNER JOIN u_teacher ut ON utt.valid = 1 AND utt.tea_id = ut.id INNER JOIN u_course uc ON uc.valid = 1 AND utt.course_id = uc.id WHERE utt.valid = 1 AND ut.tea_number = #{courseTeachingTaskDto.teaNumber}
AND utt.teaching_task_alias LIKE CONCAT('%', #{courseTeachingTaskDto.teachingTaskAlias}, '%')
AND utt.term = #{courseTeachingTaskDto.term}
AND uc.course_number LIKE CONCAT('%', #{courseTeachingTaskDto.courseNumber}, '%')
AND uc.course_name LIKE CONCAT('%', #{courseTeachingTaskDto.courseName}, '%')
AND uc.course_credit = #{courseTeachingTaskDto.courseCredit}
