mapper.select:
SELECT uttn.id, uttn.teaching_task_id, uttn.notice_title, uttn.notice_content, uttn.notice_attachment, uttn.notice_shared, uttn.notice_post_time, uttn.notice_keywords, uttn.valid, uttn.memo, uttn.create_time, uttn.create_by, uttn.update_time, uttn.update_by, uttn.notice_attachment_name FROM u_teaching_task_notice uttn INNER JOIN u_teaching_task utt ON utt.valid = 1 AND uttn.teaching_task_id = utt.id WHERE uttn.valid = 1 AND uttn.teaching_task_id = #{teachingTaskNoticeDto.teachingTaskId}
AND uttn.notice_title LIKE CONCAT('%',#{teachingTaskNoticeDto.noticeTitle}, '%')
AND uttn.notice_content LIKE CONCAT('%', #{teachingTaskNoticeDto.noticeContent}, '%')
AND uttn.notice_shared = #{teachingTaskNoticeDto.noticeShared}
AND DATE(uttn.notice_post_time) = #{teachingTaskNoticeDto.noticePostTime}
AND uttn.notice_keywords LIKE CONCAT('%', #{teachingTaskNoticeDto.noticeKeywords}, '%')
AND uttn.notice_attachment_name LIKE CONCAT('%', #{teachingTaskNoticeDto.noticeAttachmentName}, '%')
ORDER BY uttn.notice_post_time desc
LIMIT #{pageParam.startIndex}, #{pageParam.pageSize}

SELECT COUNT(1) FROM u_teaching_task_notice uttn INNER JOIN u_teaching_task utt ON utt.valid = 1 AND uttn.teaching_task_id = utt.id WHERE uttn.valid = 1 AND uttn.teaching_task_id = #{teachingTaskNoticeDto.teachingTaskId}
AND uttn.notice_title LIKE CONCAT('%',#{teachingTaskNoticeDto.noticeTitle}, '%')
AND uttn.notice_content LIKE CONCAT('%', #{teachingTaskNoticeDto.noticeContent}, '%')
AND uttn.notice_shared = #{teachingTaskNoticeDto.noticeShared}
AND DATE(uttn.notice_post_time) = #{teachingTaskNoticeDto.noticePostTime}
AND uttn.notice_keywords LIKE CONCAT('%', #{teachingTaskNoticeDto.noticeKeywords}, '%')
AND uttn.notice_attachment_name LIKE CONCAT('%', #{teachingTaskNoticeDto.noticeAttachmentName}, '%')
